exports.alterConfig = function(def, alterations){
	for(name in alterations){
		if(typeof def[name] != 'undefined')
			def[name] = alterations[name];
	}
};

exports.addConfig = function(def, additions){
	for(name in additions){
		if(typeof def[name] == 'undefined')
			def[name] = additions[name];
	}
};

exports.combineConfig = function(def, secondary){
	for(name in secondary){
		def[name] = secondary[name];
	}
};